mapper.select:
SELECT admin_id, company_id, name, email, password, phone_number, address, role, created_at, email_verified
FROM admins
WHERE admin_id = #{adminId}

SELECT admin_id, company_id, name, email, password, phone_number, address, role, created_at, email_verified
FROM admins
WHERE email = #{email}

SELECT admin_id, company_id, name, email, password, phone_number, address, role, created_at, email_verified
FROM admins
WHERE company_id = #{companyId}

SELECT COUNT(*) > 0
FROM admins
WHERE email = #{email}

mapper.update:
UPDATE admins
SET email_verified = #{verified}
WHERE email = #{email}

UPDATE admins
SET password = #{password}
WHERE admin_id = #{adminId}
