mapper.select:
SELECT COUNT(*)
FROM contract
WHERE client_id = #{clientId}
AND contract_time = #{contractTime}
AND status IN ('PENDING','IN_PROGRESS')

SELECT COUNT(*)
FROM contract
WHERE agent_id = #{agentId}
AND contract_time = #{contractTime}
AND status IN ('PENDING','IN_PROGRESS')

SELECT COUNT(*)
FROM contract
WHERE client_id = #{clientId}
AND contract_time = #{contractTime}
AND contract_id != #{excludeContractId}
AND status IN ('PENDING','IN_PROGRESS')

SELECT COUNT(*)
FROM contract
WHERE agent_id = #{agentId}
AND contract_time = #{contractTime}
AND contract_id != #{excludeContractId}
AND status IN ('PENDING','IN_PROGRESS')

mapper.update:
UPDATE contract
SET contract_time = #{contractTime},
memo = #{memo},
status = #{status}
WHERE contract_id = #{contractId}

mapper.select:
SELECT * FROM ( SELECT a.*, ROWNUM rn FROM ( SELECT c.contract_id AS contract_id, c.contract_time AS contract_time, a.agent_id AS agent_id, a.name AS agent_name, cl.client_id AS client_id, cl.name AS client_name, c.status AS status, t.contract_template_id AS contract_template_id, t.contract_name AS contract_name FROM contract c LEFT JOIN agent a ON a.agent_id = c.agent_id LEFT JOIN client cl ON cl.client_id = c.client_id LEFT JOIN contract_templates t ON c.contract_template_id = t.contract_template_id
c.company_id = #{companyId}
AND c.contract_id = #{contractId}
AND TRUNC(c.contract_time, 'MM') = TO_DATE(#{contractTime}, 'YYYY-MM')
AND c.agent_id = #{agentId}
AND c.client_id = #{clientId}
AND c.status = #{status}
AND LOWER(t.contract_name) LIKE '%' || LOWER(#{contractName}) || '%'
ORDER BY c.contract_time ASC ) a WHERE ROWNUM <= #{offset} + #{pageSize} ) WHERE rn > #{offset}

SELECT COUNT(*)
FROM contract c


	LEFT JOIN contract_templates t
	ON c.contract_template_id = t.contract_template_id


	c.company_id = #{companyId}

		AND c.contract_id = #{contractId}


		AND TRUNC(c.contract_time, 'MM') = TO_DATE(#{contractTime}, 'YYYY-MM')


		AND c.agent_id = #{agentId}


		AND c.client_id = #{clientId}


		AND c.status = #{status}


		AND LOWER(t.contract_name) LIKE '%' || LOWER(#{contractName}) || '%'

mapper.update:
UPDATE contract
SET status = #{param2, jdbcType=VARCHAR}
WHERE contract_id = #{param1}

UPDATE contract
SET memo = #{param2, jdbcType=CLOB}
WHERE contract_id = #{param1}

UPDATE contract
SET agent_id = #{agentId, jdbcType=NUMERIC}
WHERE contract_id = #{contractId}

UPDATE contract
SET client_id = #{clientId, jdbcType=NUMERIC}
WHERE contract_id = #{contractId}

UPDATE contract
SET status = 'COMPLETED',
	memo = #{param2, jdbcType=CLOB}
WHERE contract_id = #{param1}

mapper.select:

SELECT *
FROM (
SELECT
c.*,
cl.name    AS client_name,
cl.email   AS client_email,
ROW_NUMBER() OVER (
ORDER BY


        c.contract_time ASC


        c.contract_time DESC


) AS rn
FROM contract c
LEFT JOIN client cl
ON c.client_id = cl.client_id
WHERE c.agent_id = #{agentId}
AND c.status   = #{status}
) sub
WHERE sub.rn > #{offset}
AND sub.rn <= #{endIndex}

SELECT COUNT(*)
FROM contract
WHERE agent_id = #{agentId}
AND status = #{status}

SELECT pdf_id
FROM pdf
WHERE contract_id = #{contractId}

SELECT * FROM (
	SELECT , 
		   TO_CHAR(created_at, 'YYYY-MM-DD HH24:MI:SS') as created_at,
		   TO_CHAR(contract_time, 'YYYY-MM-DD HH24:MI:SS') as contract_time
	FROM contract
	WHERE agent_id = #{agentId}
	AND client_id = #{clientId}
	AND status = 'PENDING'
	ORDER BY contract_id DESC
) WHERE ROWNUM <= 1
